mapper.insert:
INSERT INTO SHOP_ITEM(
    ITEM_CODE
    , ITEM_NAME
    , ITEM_PRICE
    , ITEM_INTRO
    , CATE_CODE
)
VALUES(
    #{itemCode}
    , #{itemName}
    , #{itemPrice}
    , #{itemIntro}
    , #{cateCode}
)

mapper.select:
SELECT ITEM_CODE , ITEM_NAME , ITEM_PRICE , ITEM_STOCK , ITEM_STATUS FROM SHOP_ITEM WHERE 1 = 1
AND CATE_CODE = #{searchCateCode}
AND ITEM_STATUS IN
#{itemStatus}
AND UPPER(ITEM_NAME) LIKE '%'||UPPER(#{searchItemName})||'%'
AND ITEM_STOCK >= #{searchItemStockMin}
AND ITEM_STOCK <= #{searchItemStockMax}
ORDER BY ITEM_CODE DESC

mapper.update:
UPDATE SHOP_ITEM
SET ITEM_STOCK = #{itemStock}
WHERE ITEM_CODE = #{itemCode}

UPDATE SHOP_ITEM
SET ITEM_STATUS = #{itemStatus}
WHERE ITEM_CODE = #{itemCode}

mapper.insert:
INSERT INTO ITEM_IMG (
    IMG_CODE
    , ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , ITEM_CODE
    , IS_MAIN
)

    SELECT (SELECT 'IMG_'||LPAD((NVL(MAX(TO_NUMBER(SUBSTR(IMG_CODE, 5))),0)+1+#{idx}),3,'0')
            FROM ITEM_IMG)
        , #{img.originFileName}
        , #{img.attachedFileName}
        , #{img.itemCode}
        , #{img.isMain}
    FROM DUAL
